mapper.select:
select ti.id trackId, ti.track_title, ti.album_id, ti.cover_url, ti.media_duration, max(if(ts.stat_type='0701', ts.stat_num, 0)) playStatNum, max(if(ts.stat_type='0702', ts.stat_num, 0)) collectStatNum, max(if(ts.stat_type='0703', ts.stat_num, 0)) praiseStatNum, max(if(ts.stat_type='0704', ts.stat_num, 0)) commentStatNum from track_info ti left join track_stat ts on ts.track_id = ti.id
ti.user_id = #{vo.userId}
and ti.status = #{vo.status}
and ti.track_title like concat('%', #{vo.trackTitle}, '%')
and ti.is_deleted = 0
group by ti.id order by ti.id desc

mapper.update:
UPDATE tingshu_album.track_info
set order_num = order_num - 1
where album_id = #{albumId}
  and order_num > #{orderNum}
